import axios from "axios";
import React from "react";
import { useEffect , useState } from "react";


const Currencies = () => {

  const [valores,setValores] = useState("")
  const llamadaApi = async () => {
    try {
      const response = await axios.get(
        "https://api.nomics.com/v1/currencies/ticker?ids=BTC,ETH,SLP,SHIB&key=8fe9c5d148a1ce1866da9fc2b79933390ab02ba1"
      );
      console.log(response.data);
      setValores (response.data)
    } catch (error) {
      alert(error);
    }
  };

  useEffect(() => 
  {
    llamadaApi();
  }, []);



  return (
    <div>
      <h1>Eleg√≠ la Crypto-Moneda a cotizar</h1>
      <select value= {valores}>
        <option value= '0'>BTC</option>
        <option value= '1'>ETH</option>
        <option value= '2'>SLP</option>
        <option value= '3'>SHIB</option>
      </select>
    </div>
  );
};

export default Currencies;
