import axios from "axios";
import React from "react";
import { useEffect , useState } from "react";


const Currencies = () => {

  const [valores,setValores] = useState("")
  const [cambiadorEstado, setCambiadorEstado] = useState ('')
//   const monedaSeleccionada = 'BTC'
  const llamadaApi = async () => {

    try {
      const response = await axios.get(
        `https://api.nomics.com/v1/currencies/ticker?ids=${cambiadorEstado}&key=8fe9c5d148a1ce1866da9fc2b79933390ab02ba1`
      );
      console.log(response.data);
      setValores (response.id)
    } catch (error) {
      alert(error);
    }
  };

  useEffect(() => 
  {
    llamadaApi();
  }, []);

const handleChangeValor = (e) => {
    setCambiadorEstado(e.target.value)
    llamadaApi()
    console.log(e.target.value)
}

  return (
    <div>
      <h1>Eleg√≠ la Crypto-Moneda a cotizar</h1>
      <select onChange= {handleChangeValor}>
        <option value= 'BTC'>BTC</option>
        <option value= 'ETH'>ETH</option>
        <option value= 'SLP'>SLP</option>
        <option value= 'SHIB'>SHIB</option>
      </select>
    </div>
  );
};

export default Currencies;
